css:
.product-card__prices {
  color: var(--primary)
}
.product__prices {
color: var(--primary)
}
.product-card__name {
  font-weight: bold;
}
#product-description .lead {
font-weight: bold
}
.prices__discount span {
  font-weight: bold !important;
  color: #6c757d !important;
}
.menu__list:before {
    content: url (template/public/img/uploads/marca_dagua_1.png);
   display: flex;
  justify-content: center
}
.breadcrumb-item span {
color: var(--primary)
}
.product-card__rating + .badge-dark, .product__rating + .product__out-of-stock  {
  font-size: 0px;
}
.product-card__rating + .badge-dark:before {
  color: #fff;
  background-color: #343a40;
  content: 'Esgotado';
  font-size: 1rem !important;
  display: inline-block;
  padding: .25em .4em;
  font-weight: 700;
  line-height: 1;
  text-align: center;
  white-space: nowrap;
  vertical-align: baseline;
  border-radius: .25rem;
}
.product__rating + .product__out-of-stock:before {
  content: 'Esgotado';
  font-weight: var(--font-bold);
  font-size: var(--font-size-lg);
  color: var(--warning);
}
